sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (newValue == 1) {
        g_form.setSectionDisplay('priority_1', true);
        g_form.setSectionDisplay('priority_2', false);
		g_form.setSectionDisplay('priority_3', false);
    }
	if (newValue == 2) {
		g_form.setSectionDisplay('priority_1', false);
        g_form.setSectionDisplay('priority_2', true);
		g_form.setSectionDisplay('priority_3', false);
	}
	if (newValue == 3) {
		g_form.setSectionDisplay('priority_1', false);
        g_form.setSectionDisplay('priority_2', false);
		g_form.setSectionDisplay('priority_3', true);
	}
	if (newValue == 3) {
		g_form.setSectionDisplay('priority_1', false);
        g_form.setSectionDisplay('priority_2', false);
		g_form.setSectionDisplay('priority_3', true);
	}
	if (newValue == 'subflow') {
		g_form.setSectionDisplay('priority_1', true);
        g_form.setSectionDisplay('priority_2', true);
		g_form.setSectionDisplay('priority_3', true);
	}
	if (newValue == '') {
        g_form.setSectionDisplay('priority_1', true);
        g_form.setSectionDisplay('priority_2', true);
		g_form.setSectionDisplay('priority_3', true);
    }
//Type appropriate comment here, and begin script below

}
